import React, { useState } from "react"
import "./Tab.css";
import ListProducts from "./ListProducts";

const GalleryReact = () => {
	const [items, setItems] = useState(ListProducts);

	const filterItem = (categItem) => {
		const updatedItems = ListProducts.filter((curElem) => {
			return curElem.category === categItem;
		});
		setItems(updatedItems);
	};
	return (
		<div>
			<h1 className="mt-5 text-center main-heading">

			</h1>
			<hr />
			<div className="menu-tabs container">
				<div className="menu-tab d-flex justify-content-around">
					<button className="btn btn-warning" onClick={() => filterItem("res")}>
						Res
					</button>
					<button	className="btn btn-warning"	onClick={() => filterItem("pollo")}
					>
						Pollo
					</button>
					<button
						className="btn btn-warning"
						onClick={() => filterItem("cerdo")}
					>
						Cerdo
					</button>
					<button
						className="btn btn-warning"
						onClick={() => filterItem("queso")}
					>
						Quesos
					</button>
					<button
						className="btn btn-warning"
						onClick={() => setItems(ListProducts)}
					>
						All
					</button>
				</div>
			</div>

			{/* my main items section */}
			<div className="menu-items container-fluid mt-5">
				<div className="row">
					<div className="col-11 mx-auto">
						<div className="row my-5">
							{items.map((elem) => {
								const { product, image, price } = elem;
								return (
									<div className="item1 col-12 col-md-6 col-lg-6 col-xl-3 my-4  py-4">
									<h4 className=" d-flex justify-content-center  ">{product}</h4>
										<div className="row Item-inside d-flex justify-conten-center  ">

											{/* for image */}
											<div className="col-12 col-md-12 col-lg-12 img-div ">
												<img
													src={(image)}
													alt=""
													className="img-fluid image-product"
												/>
											</div>

											{/* menu items description */}
											<div className="col-12 col-md-12 col-lg-8">
												<div className="main-title pt-4 pb-3"></div>
												<div className="menu-price-book d-flex justify-conten-center">
													<div className="price-book-divide ">
														<h4>
															${price}
															<b>/Lb</b>{" "}
														</h4>
														<a href="#">
															<button className="btn btn-primary buttonCartShopping p-1 ">
																AÃ±adir al carrito
															</button>
														</a>

													</div>

												</div>
											</div>
										</div>
									</div>
								);
							})}
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default GalleryReact;
